INSERT INTO 'CTCAE_Toxicities' ('Code', 'Description', 'Term', 'Grade1Description', 'Grade2Description', 'Grade3Description', 'Grade4Description', 'Grade5Description', 'Definition', 'LastUpdated') VALUES
(10002272, 'Blood and lymphatic system disorders', 'Anemia', 'Hemoglobin (Hgb) <LLN - 10.0 g/dL; <LLN - 6.2 mmol/L; <LLN - 100 g/L', 'Hgb <10.0 - 8.0 g/dL; <6.2 - 4.9 mmol/L;  <100 - 80g/L', 'Hgb <8.0 g/dL; <4.9 mmol/L; <80 g/L; transfusion indicated', 'Life-threatening consequences; urgent intervention indicated', 'Death', 'A disorder characterized by an reduction in the amount of hemoglobin in 100 ml of blood. Signs and symptoms of anemia may include pallor of the skin and mucous membranes, shortness of breath, palpitations of the heart, soft systolic murmurs, lethargy, and fatigability.', '0000-00-00 00:00:00'),
(10048580, 'Blood and lymphatic system disorders', 'Bone marrow hypocellular', 'Mildly hypocellular or <=25% reduction from normal cellularity for age', 'Moderately hypocellular or >25 - <50% reduction from normal cellularity for age', 'Severely hypocellular or >50 - <=75% reduction cellularity from normal for age', 'Aplastic persistent for longer than 2 weeks', 'Death', 'A disorder characterized by the inability of the bone marrow to produce hematopoietic elements.', '0000-00-00 00:00:00'),
(10013442, 'Blood and lymphatic system disorders', 'Disseminated intravascular coagulation', ' -', 'Laboratory findings with no bleeding', 'Laboratory findings and bleeding', 'Life-threatening consequences; urgent intervention indicated', 'Death', 'A disorder characterized by systemic pathological activation of blood clotting mechanisms which results in clot formation throughout the body. There is an increase in the risk of hemorrhage as the body is depleted of platelets and coagulation factors.', '0000-00-00 00:00:00'),
(10016288, 'Blood and lymphatic system disorders', 'Febrile neutropenia', ' -', ' -', 'ANC <1000/mm3 with a single temperature of >38.3 degrees C (101 degrees F) or a sustained temperature of >=38 degrees C (100.4 degrees F) for more than one hour', 'Life-threatening consequences; urgent intervention indicated', 'Death', 'A disorder characterized by an ANC <1000/mm3 and a single temperature of >38.3 degrees C (101 degrees F) or a sustained temperature of >=38 degrees C (100.4 degrees F) for more than one hour', '0000-00-00 00:00:00'),
(10019491, 'Blood and lymphatic system disorders', 'Hemolysis', 'Laboratory evidence of hemolysis only (e.g., direct antiglobulin test; DAT; Coombs''; schistocytes; decreased haptoglobin)', 'Evidence of hemolysis and >=2 gm decrease in hemoglobin', 'Transfusion or medical intervention indicated (e.g., steroids)', 'Life-threatening consequences; urgent intervention indicated', 'Death', 'A disorder characterized by laboratory test results that indicate widespread erythrocyte cell membrane destruction.', '0000-00-00 00:00:00'),
(10019515, 'Blood and lymphatic system disorders', 'Hemolytic uremic syndrome', 'Evidence of RBC destruction (schistocytosis) without clinical consequences', ' -', 'Laboratory findings with clinical consequences (e.g., renal insufficiency, petechiae)', 'Life-threatening consequences, (e.g., CNS hemorrhage or thrombosis/embolism or renal failure)', 'Death', 'A disorder characterized by a form of thrombotic microangiopathy with renal failure, hemolytic anemia, and severe thrombocytopenia.', '0000-00-00 00:00:00'),
(10024378, 'Blood and lymphatic system disorders', 'Leukocytosis', ' -', ' -', '>100,000/mm3', 'Clinical manifestations of leucostasis; urgent intervention indicated', 'Death', 'A disorder characterized by laboratory test results that indicate an increased number of white blood cells in the blood.', '0000-00-00 00:00:00'),
(10025182, 'Blood and lymphatic system disorders', 'Lymph node pain', 'Mild pain', 'Moderate pain; limiting instrumental ADL', 'Severe pain; limiting self care ADL', ' -', '', 'A disorder characterized by a sensation of marked discomfort in a lymph node.', '0000-00-00 00:00:00'),
(10041633, 'Blood and lymphatic system disorders', 'Spleen disorder', 'Incidental findings (e.g., Howell-Jolly bodies); mild degree of thrombocytosis and leukocytosis', 'Prophylactic antibiotics indicated', ' -', 'Life-threatening consequences; urgent intervention indicated', 'Death', 'A disorder of the spleen.', '0000-00-00 00:00:00'),
(10043648, 'Blood and lymphatic system disorders', 'Thrombotic thrombocytopenic purpura', 'Evidence of RBC destruction (schistocytosis) without clinical consequences', ' -', 'Laboratory findings with clinical consequences (e.g., renal insufficiency, petechiae)', 'Life-threatening consequences, (e.g., CNS hemorrhage or thrombosis/embolism or renal failure)', 'Death', 'A disorder characterized by the presence of microangiopathic hemolytic anemia, thrombocytopenic purpura, fever, renal abnormalities and neurological abnormalities such as seizures, hemiplegia, and visual disturbances. It is an acute or subacute condition.', '0000-00-00 00:00:00'),
(10005329, 'Blood and lymphatic system disorders', 'Blood and lymphatic system disorders - Other, specify', 'Asymptomatic or mild symptoms; clinical or diagnostic observations only; intervention not indicated', 'Moderate; minimal, local or noninvasive intervention indicated; limiting age-appropriate instrumental ADL', 'Severe or medically significant but not immediately life-threatening; hospitalization or prolongation of existing hospitalization indicated; disabling;  limiting self care ADL', 'Life-threatening consequences; urgent intervention indicated', 'Death', '', '0000-00-00 00:00:00'),
(10051592, 'Cardiac disorders', 'Acute coronary syndrome', ' -', 'Symptomatic, progressive angina; cardiac enzymes normal; hemodynamically stable', 'Symptomatic, unstable angina and/or acute myocardial infarction, cardiac enzymes abnormal, hemodynamically stable', 'Symptomatic, unstable angina and/or acute myocardial infarction, cardiac enzymes abnormal, hemodynamically unstable', 'Death', 'A disorder characterized by signs and symptoms related to acute ischemia of the myocardium secondary to coronary artery disease. The clinical presentation covers a spectrum of heart diseases from unstable angina to myocardial infarction.', '0000-00-00 00:00:00'),
