INSERT INTO 'Question' ('QuestionSerNum', 'QuestionName', 'QuestionName_FR', 'QuestionDescription', 'QuestionDefinition', 'QuestionQuestion', 'QuestionQuestion_FR', 'SourceSerNum', 'QuestionTypeSerNum', 'isPositiveQuestion', 'LastUpdated') VALUES
(1, 'Anemia', '', 'Blood and lymphatic system disorders', 'A disorder characterized by an reduction in the amount of hemoglobin in 100 ml of blood. Signs and symptoms of anemia may include pallor of the skin and mucous membranes, shortness of breath, palpitations of the heart, soft systolic murmurs, lethargy, and fatigability.', 'Please rate the following on a scale from 1 to 5: Anemia', '', 1, 1, 0, '0000-00-00 00:00:00'),
(2, 'Bone marrow hypocellular', '', 'Blood and lymphatic system disorders', 'A disorder characterized by the inability of the bone marrow to produce hematopoietic elements.', 'Please rate the following on a scale from 1 to 5: Bone marrow hypocellular', '', 1, 1, 0, '0000-00-00 00:00:00'),
(3, 'Disseminated intravascular coagulation', '', 'Blood and lymphatic system disorders', 'A disorder characterized by systemic pathological activation of blood clotting mechanisms which results in clot formation throughout the body. There is an increase in the risk of hemorrhage as the body is depleted of platelets and coagulation factors.', 'Please rate the following on a scale from 1 to 5: Disseminated intravascular coagulation', '', 1, 1, 0, '0000-00-00 00:00:00'),
(4, 'Febrile neutropenia', '', 'Blood and lymphatic system disorders', 'A disorder characterized by an ANC <1000/mm3 and a single temperature of >38.3 degrees C (101 degrees F) or a sustained temperature of >=38 degrees C (100.4 degrees F) for more than one hour', 'Please rate the following on a scale from 1 to 5: Febrile neutropenia', '', 1, 1, 0, '0000-00-00 00:00:00'),
(5, 'Hemolysis', '', 'Blood and lymphatic system disorders', 'A disorder characterized by laboratory test results that indicate widespread erythrocyte cell membrane destruction.', 'Please rate the following on a scale from 1 to 5: Hemolysis', '', 1, 1, 0, '0000-00-00 00:00:00'),
(6, 'Hemolytic uremic syndrome', '', 'Blood and lymphatic system disorders', 'A disorder characterized by a form of thrombotic microangiopathy with renal failure, hemolytic anemia, and severe thrombocytopenia.', 'Please rate the following on a scale from 1 to 5: Hemolytic uremic syndrome', '', 1, 1, 0, '0000-00-00 00:00:00'),
(7, 'Leukocytosis', '', 'Blood and lymphatic system disorders', 'A disorder characterized by laboratory test results that indicate an increased number of white blood cells in the blood.', 'Please rate the following on a scale from 1 to 5: Leukocytosis', '', 1, 1, 0, '0000-00-00 00:00:00'),
(8, 'Lymph node pain', '', 'Blood and lymphatic system disorders', 'A disorder characterized by a sensation of marked discomfort in a lymph node.', 'Please rate the following on a scale from 1 to 5: Lymph node pain', '', 1, 1, 0, '0000-00-00 00:00:00'),
(9, 'Spleen disorder', '', 'Blood and lymphatic system disorders', 'A disorder of the spleen.', 'Please rate the following on a scale from 1 to 5: Spleen disorder', '', 1, 1, 0, '0000-00-00 00:00:00'),
(10, 'Thrombotic thrombocytopenic purpura', '', 'Blood and lymphatic system disorders', 'A disorder characterized by the presence of microangiopathic hemolytic anemia, thrombocytopenic purpura, fever, renal abnormalities and neurological abnormalities such as seizures, hemiplegia, and visual disturbances. It is an acute or subacute condition.', 'Please rate the following on a scale from 1 to 5: Thrombotic thrombocytopenic purpura', '', 1, 1, 0, '0000-00-00 00:00:00'),
(11, 'Blood and lymphatic system disorders - Other, specify', '', 'Blood and lymphatic system disorders', '', 'Please rate the following on a scale from 1 to 5: Blood and lymphatic system disorders - Other, specify', '', 1, 1, 0, '0000-00-00 00:00:00'),
(12, 'Acute coronary syndrome', '', 'Cardiac disorders', 'A disorder characterized by signs and symptoms related to acute ischemia of the myocardium secondary to coronary artery disease. The clinical presentation covers a spectrum of heart diseases from unstable angina to myocardial infarction.', 'Please rate the following on a scale from 1 to 5: Acute coronary syndrome', '', 1, 1, 0, '0000-00-00 00:00:00'),
(13, 'Aortic valve disease', '', 'Cardiac disorders', 'A disorder characterized by a defect in aortic valve function or structure.', 'Please rate the following on a scale from 1 to 5: Aortic valve disease', '', 1, 1, 0, '0000-00-00 00:00:00'),
(14, 'Asystole', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia without cardiac electrical activity. Typically, this is accompanied by cessation of the pumping function of the heart.', 'Please rate the following on a scale from 1 to 5: Asystole', '', 1, 1, 0, '0000-00-00 00:00:00'),
(15, 'Atrial fibrillation', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia without discernible P waves and an irregular ventricular response due to multiple reentry circuits. The rhythm disturbance originates above the ventricles.', 'Please rate the following on a scale from 1 to 5: Atrial fibrillation', '', 1, 1, 0, '0000-00-00 00:00:00'),
(16, 'Atrial flutter', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia with organized rhythmic atrial contractions with a rate of 200-300 beats per minute. The rhythm disturbance originates in the atria.', 'Please rate the following on a scale from 1 to 5: Atrial flutter', '', 1, 1, 0, '0000-00-00 00:00:00'),
(17, 'Atrioventricular block complete', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia with complete failure of atrial electrical impulse conduction through the AV node to the ventricles.', 'Please rate the following on a scale from 1 to 5: Atrioventricular block complete', '', 1, 1, 0, '0000-00-00 00:00:00'),
(18, 'Atrioventricular block first degree', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia with a delay in the time required for the conduction of an electrical impulse through the atrioventricular (AV) node beyond 0.2 seconds; prolongation of the PR interval greater than 200 milliseconds.', 'Please rate the following on a scale from 1 to 5: Atrioventricular block first degree', '', 1, 1, 0, '0000-00-00 00:00:00'),
(19, 'Cardiac arrest', '', 'Cardiac disorders', 'A disorder characterized by cessation of the pumping function of the heart.', 'Please rate the following on a scale from 1 to 5: Cardiac arrest', '', 1, 1, 0, '0000-00-00 00:00:00'),
(20, 'Chest pain - cardiac', '', 'Cardiac disorders', 'A disorder characterized by substernal discomfort due to insufficient myocardial oxygenation.', 'Please rate the following on a scale from 1 to 5: Chest pain - cardiac', '', 1, 1, 0, '0000-00-00 00:00:00'),
(21, 'Conduction disorder', '', 'Cardiac disorders', 'A disorder characterized by pathological irregularities in the cardiac conduction system.', 'Please rate the following on a scale from 1 to 5: Conduction disorder', '', 1, 1, 0, '0000-00-00 00:00:00'),
(22, 'Constrictive pericarditis', '', 'Cardiac disorders', 'A disorder characterized by a thickened and fibrotic pericardial sac; these fibrotic changes impede normal myocardial function by restricting myocardial muscle action.', 'Please rate the following on a scale from 1 to 5: Constrictive pericarditis', '', 1, 1, 0, '0000-00-00 00:00:00'),
(23, 'Heart failure', '', 'Cardiac disorders', 'A disorder characterized by the inability of the heart to pump blood at an adequate volume to meet tissue metabolic requirements, or, the ability to do so only at an elevation in the filling pressure.', 'Please rate the following on a scale from 1 to 5: Heart failure', '', 1, 1, 0, '0000-00-00 00:00:00'),
(24, 'Left ventricular systolic dysfunction', '', 'Cardiac disorders', 'A disorder characterized by failure of the left ventricle to produce adequate output despite an increase in distending pressure and in end-diastolic volume. Clinical manifestations may include dyspnea, orthopnea, and other signs and symptoms of pulmonary congestion and edema.', 'Please rate the following on a scale from 1 to 5: Left ventricular systolic dysfunction', '', 1, 1, 0, '0000-00-00 00:00:00'),
(25, 'Mitral valve disease', '', 'Cardiac disorders', 'A disorder characterized by a defect in mitral valve function or structure.', 'Please rate the following on a scale from 1 to 5: Mitral valve disease', '', 1, 1, 0, '0000-00-00 00:00:00'),
(26, 'Mobitz (type) II atrioventricular block', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia with relatively constant PR interval prior to the block of an atrial impulse. This is the result of intermittent failure of atrial electrical impulse conduction through the atrioventricular (AV) node to the ventricles.', 'Please rate the following on a scale from 1 to 5: Mobitz (type) II atrioventricular block', '', 1, 1, 0, '0000-00-00 00:00:00'),
(27, 'Mobitz type I', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia with a progressively lengthening PR interval prior to the blocking of an atrial impulse. This is the result of intermittent failure of atrial electrical impulse conduction through the atrioventricular (AV) node to the ventricles.', 'Please rate the following on a scale from 1 to 5: Mobitz type I', '', 1, 1, 0, '0000-00-00 00:00:00'),
(28, 'Myocardial infarction', '', 'Cardiac disorders', 'A disorder characterized by gross necrosis of the myocardium; this is due to an interruption of blood supply to the area.', 'Please rate the following on a scale from 1 to 5: Myocardial infarction', '', 1, 1, 0, '0000-00-00 00:00:00'),
(29, 'Myocarditis', '', 'Cardiac disorders', 'A disorder characterized by inflammation of the muscle tissue of the heart.', 'Please rate the following on a scale from 1 to 5: Myocarditis', '', 1, 1, 0, '0000-00-00 00:00:00'),
(30, 'Palpitations', '', 'Cardiac disorders', 'A disorder characterized by an unpleasant sensation of irregular and/or forceful beating of the heart.', 'Please rate the following on a scale from 1 to 5: Palpitations', '', 1, 1, 0, '0000-00-00 00:00:00'),
(31, 'Paroxysmal atrial tachycardia', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia with abrupt onset and sudden termination of atrial contractions with a rate of 150-250 beats per minute. The rhythm disturbance originates in the atria.', 'Please rate the following on a scale from 1 to 5: Paroxysmal atrial tachycardia', '', 1, 1, 0, '0000-00-00 00:00:00'),
(32, 'Pericardial effusion', '', 'Cardiac disorders', 'A disorder characterized by fluid collection within the pericardial sac, usually due to inflammation.', 'Please rate the following on a scale from 1 to 5: Pericardial effusion', '', 1, 1, 0, '0000-00-00 00:00:00'),
(33, 'Pericardial tamponade', '', 'Cardiac disorders', 'A disorder characterized by an increase in intrapericardial pressure due to the collection of blood or fluid in the pericardium.', 'Please rate the following on a scale from 1 to 5: Pericardial tamponade', '', 1, 1, 0, '0000-00-00 00:00:00'),
(34, 'Pericarditis', '', 'Cardiac disorders', 'A disorder characterized by irritation to the layers of the pericardium (the protective sac around the heart).', 'Please rate the following on a scale from 1 to 5: Pericarditis', '', 1, 1, 0, '0000-00-00 00:00:00'),
(35, 'Pulmonary valve disease', '', 'Cardiac disorders', 'A disorder characterized by a defect in pulmonary valve function or structure.', 'Please rate the following on a scale from 1 to 5: Pulmonary valve disease', '', 1, 1, 0, '0000-00-00 00:00:00'),
(36, 'Restrictive cardiomyopathy', '', 'Cardiac disorders', 'A disorder characterized by an inability of the ventricles to fill with blood because the myocardium (heart muscle) stiffens and loses its flexibility.', 'Please rate the following on a scale from 1 to 5: Restrictive cardiomyopathy', '', 1, 1, 0, '0000-00-00 00:00:00'),
(37, 'Right ventricular dysfunction', '', 'Cardiac disorders', 'A disorder characterized by impairment of right ventricular function associated with low ejection fraction and a decrease in motility of the right ventricular wall.', 'Please rate the following on a scale from 1 to 5: Right ventricular dysfunction', '', 1, 1, 0, '0000-00-00 00:00:00'),
(38, 'Sick sinus syndrome', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia with alternating periods of bradycardia and atrial tachycardia accompanied by syncope, fatigue and dizziness.', 'Please rate the following on a scale from 1 to 5: Sick sinus syndrome', '', 1, 1, 0, '0000-00-00 00:00:00'),
(39, 'Sinus bradycardia', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia with a heart rate less than 60 beats per minute that originates in the sinus node.', 'Please rate the following on a scale from 1 to 5: Sinus bradycardia', '', 1, 1, 0, '0000-00-00 00:00:00'),
(40, 'Sinus tachycardia', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia with a heart rate greater than 100 beats per minute that originates in the sinus node.', 'Please rate the following on a scale from 1 to 5: Sinus tachycardia', '', 1, 1, 0, '0000-00-00 00:00:00'),
(41, 'Supraventricular tachycardia', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia with a heart rate greater than 100 beats per minute that originates above the ventricles.', 'Please rate the following on a scale from 1 to 5: Supraventricular tachycardia', '', 1, 1, 0, '0000-00-00 00:00:00'),
(42, 'Tricuspid valve disease', '', 'Cardiac disorders', 'A disorder characterized by a defect in tricuspid valve function or structure.', 'Please rate the following on a scale from 1 to 5: Tricuspid valve disease', '', 1, 1, 0, '0000-00-00 00:00:00'),
(43, 'Ventricular arrhythmia', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia that originates in the ventricles.', 'Please rate the following on a scale from 1 to 5: Ventricular arrhythmia', '', 1, 1, 0, '0000-00-00 00:00:00'),
(44, 'Ventricular fibrillation', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia without discernible QRS complexes due to rapid repetitive excitation of myocardial fibers without coordinated contraction of the ventricles.', 'Please rate the following on a scale from 1 to 5: Ventricular fibrillation', '', 1, 1, 0, '0000-00-00 00:00:00'),
(45, 'Ventricular tachycardia', '', 'Cardiac disorders', 'A disorder characterized by a dysrhythmia with a heart rate greater than 100 beats per minute that originates distal to the bundle of His.', 'Please rate the following on a scale from 1 to 5: Ventricular tachycardia', '', 1, 1, 0, '0000-00-00 00:00:00'),
(46, 'Wolff-Parkinson-White syndrome', '', 'Cardiac disorders', 'A disorder characterized by the presence of an accessory conductive pathway between the atria and the ventricles that causes premature ventricular activation.', 'Please rate the following on a scale from 1 to 5: Wolff-Parkinson-White syndrome', '', 1, 1, 0, '0000-00-00 00:00:00'),
(47, 'Cardiac disorders - Other, specify', '', 'Cardiac disorders', '', 'Please rate the following on a scale from 1 to 5: Cardiac disorders - Other, specify', '', 1, 1, 0, '0000-00-00 00:00:00'),
(48, 'Congenital, familial and genetic disorders - Other, specify', '', 'Congenital, familial and genetic disorders', '', 'Please rate the following on a scale from 1 to 5: Congenital, familial and genetic disorders - Other, specify', '', 1, 1, 0, '0000-00-00 00:00:00'),
(49, 'Ear pain', '', 'Ear and labyrinth disorders', 'A disorder characterized by a sensation of marked discomfort in the ear.', 'Please rate the following on a scale from 1 to 5: Ear pain', '', 1, 1, 0, '0000-00-00 00:00:00'),
(50, 'External ear inflammation', '', 'Ear and labyrinth disorders', 'A disorder characterized by inflammation, swelling and redness to the outer ear and ear canal.', 'Please rate the following on a scale from 1 to 5: External ear inflammation', '', 1, 1, 0, '0000-00-00 00:00:00'),
(51, 'External ear pain', '', 'Ear and labyrinth disorders', 'A disorder characterized by a sensation of marked discomfort in the external ear region.', 'Please rate the following on a scale from 1 to 5: External ear pain', '', 1, 1, 0, '0000-00-00 00:00:00'),
(52, 'Hearing impaired', '', 'Ear and labyrinth disorders', 'A disorder characterized by partial or complete loss of the ability to detect or understand sounds resulting from damage to ear structures.', 'Please rate the following on a scale from 1 to 5: Hearing impaired', '', 1, 1, 0, '0000-00-00 00:00:00'),
(53, 'Middle ear inflammation', '', 'Ear and labyrinth disorders', 'A disorder characterized by inflammation (physiologic response to irritation), swelling and redness to the middle ear.', 'Please rate the following on a scale from 1 to 5: Middle ear inflammation', '', 1, 1, 0, '0000-00-00 00:00:00'),
(54, 'Tinnitus', '', 'Ear and labyrinth disorders', 'A disorder characterized by noise in the ears, such as ringing, buzzing, roaring or clicking.', 'Please rate the following on a scale from 1 to 5: Tinnitus', '', 1, 1, 0, '0000-00-00 00:00:00'),
(55, 'Vertigo', '', 'Ear and labyrinth disorders', 'A disorder characterized by a sensation as if the external world were revolving around the patient (objective vertigo) or as if he himself were revolving in space (subjective vertigo).', 'Please rate the following on a scale from 1 to 5: Vertigo', '', 1, 1, 0, '0000-00-00 00:00:00'),
(56, 'Vestibular disorder', '', 'Ear and labyrinth disorders', 'A disorder characterized by dizziness, imbalance, nausea, and vision problems.', 'Please rate the following on a scale from 1 to 5: Vestibular disorder', '', 1, 1, 0, '0000-00-00 00:00:00'),
(57, 'Ear and labyrinth disorders - Other, specify', '', 'Ear and labyrinth disorders', '', 'Please rate the following on a scale from 1 to 5: Ear and labyrinth disorders - Other, specify', '', 1, 1, 0, '0000-00-00 00:00:00'),
(58, 'Adrenal insufficiency', '', 'Endocrine disorders', 'A disorder that occurs when the adrenal cortex does not produce enough of the hormone cortisol and in some cases, the hormone aldosterone. It may be due to a disorder of the adrenal cortex as in Addison''s disease or primary adrenal insufficiency.', 'Please rate the following on a scale from 1 to 5: Adrenal insufficiency', '', 1, 1, 0, '0000-00-00 00:00:00'),
(59, 'Cushingoid', '', 'Endocrine disorders', 'A disorder characterized by signs and symptoms that resemble Cushing''s disease or syndrome: buffalo hump obesity, striations, adiposity, hypertension, diabetes, and osteoporosis, usually due to exogenous corticosteroids.', 'Please rate the following on a scale from 1 to 5: Cushingoid', '', 1, 1, 0, '0000-00-00 00:00:00'),
(60, 'Delayed puberty', '', 'Endocrine disorders', 'A disorder characterized by unusually late sexual maturity.', 'Please rate the following on a scale from 1 to 5: Delayed puberty', '', 1, 1, 0, '0000-00-00 00:00:00'),
(61, 'Growth accelerated', '', 'Endocrine disorders', 'A disorder characterized by greater growth than expected for age.', 'Please rate the following on a scale from 1 to 5: Growth accelerated', '', 1, 1, 0, '0000-00-00 00:00:00'),
(62, 'Hyperparathyroidism', '', 'Endocrine disorders', 'A disorder characterized by an increase in production of parathyroid hormone by the parathyroid glands. This results in hypercalcemia (abnormally high levels of calcium in the blood).', 'Please rate the following on a scale from 1 to 5: Hyperparathyroidism', '', 1, 1, 0, '0000-00-00 00:00:00'),
(63, 'Hyperthyroidism', '', 'Endocrine disorders', 'A disorder characterized by excessive levels of thyroid hormone in the body. Common causes include an overactive thyroid gland or thyroid hormone overdose.', 'Please rate the following on a scale from 1 to 5: Hyperthyroidism', '', 1, 1, 0, '0000-00-00 00:00:00'),
(64, 'Hypoparathyroidism', '', 'Endocrine disorders', 'A disorder characterized by a decrease in production of parathyroid hormone by the parathyroid glands.', 'Please rate the following on a scale from 1 to 5: Hypoparathyroidism', '', 1, 1, 0, '0000-00-00 00:00:00'),
(65, 'Hypothyroidism', '', 'Endocrine disorders', 'A disorder characterized by a decrease in production of thyroid hormone by the thyroid gland.', 'Please rate the following on a scale from 1 to 5: Hypothyroidism', '', 1, 1, 0, '0000-00-00 00:00:00'),
(66, 'Precocious puberty', '', 'Endocrine disorders', 'A disorder characterized by unusually early development of secondary sexual features; the onset of sexual maturation begins usually before age 8 for girls and before age 9 for boys.', 'Please rate the following on a scale from 1 to 5: Precocious puberty', '', 1, 1, 0, '0000-00-00 00:00:00'),
(67, 'Virilization', '', 'Endocrine disorders', 'A disorder characterized by inappropriate masculinization occurring in a female or prepubertal male.', 'Please rate the following on a scale from 1 to 5: Virilization', '', 1, 1, 0, '0000-00-00 00:00:00'),
(68, 'Endocrine disorders - Other, specify', '', 'Endocrine disorders', '', 'Please rate the following on a scale from 1 to 5: Endocrine disorders - Other, specify', '', 1, 1, 0, '0000-00-00 00:00:00'),
(69, 'Blurred vision', '', 'Eye disorders', 'A disorder characterized by visual perception of unclear or fuzzy images.', 'Please rate the following on a scale from 1 to 5: Blurred vision', '', 1, 1, 0, '0000-00-00 00:00:00'),
