(606, 'Psychiatric disorders - Other, specify', '', 'Psychiatric disorders', ' -', 'Please rate the following on a scale from 1 to 5: Psychiatric disorders - Other, specify', '', 1, 1, 0, '0000-00-00 00:00:00'),
(607, 'Acute kidney injury', '', 'Renal and urinary disorders', 'A disorder characterized by the acute loss of renal function and is traditionally classified as pre-renal (low blood flow into kidney), renal (kidney damage) and post-renal causes (ureteral or bladder outflow obstruction).', 'Please rate the following on a scale from 1 to 5: Acute kidney injury', '', 1, 1, 0, '0000-00-00 00:00:00'),
(608, 'Bladder perforation', '', 'Renal and urinary disorders', 'A disorder characterized by a rupture in the bladder wall.', 'Please rate the following on a scale from 1 to 5: Bladder perforation', '', 1, 1, 0, '0000-00-00 00:00:00'),
(609, 'Bladder spasm', '', 'Renal and urinary disorders', 'A disorder characterized by a sudden and involuntary contraction of the bladder wall.', 'Please rate the following on a scale from 1 to 5: Bladder spasm', '', 1, 1, 0, '0000-00-00 00:00:00'),
(610, 'Chronic kidney disease', '', 'Renal and urinary disorders', 'A disorder characterized by gradual and usually permanent loss of kidney function resulting in renal failure.', 'Please rate the following on a scale from 1 to 5: Chronic kidney disease', '', 1, 1, 0, '0000-00-00 00:00:00'),
(611, 'Cystitis noninfective', '', 'Renal and urinary disorders', 'A disorder characterized by inflammation of the bladder which is not caused by an infection of the urinary tract.', 'Please rate the following on a scale from 1 to 5: Cystitis noninfective', '', 1, 1, 0, '0000-00-00 00:00:00'),
