(709, 'Pharyngeal mucositis', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by an inflammation involving the mucous membrane of the pharynx.', 'Please rate the following on a scale from 1 to 5: Pharyngeal mucositis', '', 1, 1, 0, '0000-00-00 00:00:00'),
(710, 'Pharyngeal necrosis', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by a necrotic process occurring in the pharynx.', 'Please rate the following on a scale from 1 to 5: Pharyngeal necrosis', '', 1, 1, 0, '0000-00-00 00:00:00'),
(711, 'Pharyngeal stenosis', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by a narrowing of the pharyngeal airway.', 'Please rate the following on a scale from 1 to 5: Pharyngeal stenosis', '', 1, 1, 0, '0000-00-00 00:00:00'),
