(715, 'Pleuritic pain', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by marked discomfort sensation in the pleura.', 'Please rate the following on a scale from 1 to 5: Pleuritic pain', '', 1, 1, 0, '0000-00-00 00:00:00'),
(716, 'Pneumonitis', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by inflammation focally or diffusely affecting the lung parenchyma.', 'Please rate the following on a scale from 1 to 5: Pneumonitis', '', 1, 1, 0, '0000-00-00 00:00:00'),
(717, 'Pneumothorax', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by abnormal presence of air in the pleural cavity resulting in the collapse of the lung.', 'Please rate the following on a scale from 1 to 5: Pneumothorax', '', 1, 1, 0, '0000-00-00 00:00:00'),
(718, 'Postnasal drip', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by excessive mucous secretion in the back of the nasal cavity or throat, causing sore throat and/or coughing.', 'Please rate the following on a scale from 1 to 5: Postnasal drip', '', 1, 1, 0, '0000-00-00 00:00:00'),
(719, 'Productive cough', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by expectorated secretions upon coughing.', 'Please rate the following on a scale from 1 to 5: Productive cough', '', 1, 1, 0, '0000-00-00 00:00:00'),
(720, 'Pulmonary edema', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by accumulation of fluid in the lung tissues that causes a disturbance of the gas exchange that may lead to respiratory failure.', 'Please rate the following on a scale from 1 to 5: Pulmonary edema', '', 1, 1, 0, '0000-00-00 00:00:00'),
(721, 'Pulmonary fibrosis', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by the replacement of the lung tissue by connective tissue, leading to progressive dyspnea, respiratory failure or right heart failure.', 'Please rate the following on a scale from 1 to 5: Pulmonary fibrosis', '', 1, 1, 0, '0000-00-00 00:00:00'),
(722, 'Pulmonary fistula', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by an abnormal communication between the lung and another organ or anatomic site.', 'Please rate the following on a scale from 1 to 5: Pulmonary fistula', '', 1, 1, 0, '0000-00-00 00:00:00'),
(723, 'Pulmonary hypertension', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by an increase in pressure within the pulmonary circulation due to lung or heart disorder.', 'Please rate the following on a scale from 1 to 5: Pulmonary hypertension', '', 1, 1, 0, '0000-00-00 00:00:00'),
(724, 'Respiratory failure', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by impaired gas exchange by the respiratory system resulting in hypoxemia and a decrease in oxygenation of the tissues that may be associated with an increase in arterial levels of carbon dioxide.', 'Please rate the following on a scale from 1 to 5: Respiratory failure', '', 1, 1, 0, '0000-00-00 00:00:00'),
(725, 'Retinoic acid syndrome', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by weight gain, dyspnea, pleural and pericardial effusions, leukocytosis and/or renal failure originally described in patients treated with all-trans retinoic acid.', 'Please rate the following on a scale from 1 to 5: Retinoic acid syndrome', '', 1, 1, 0, '0000-00-00 00:00:00'),
(726, 'Sinus disorder', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by involvement of the paranasal sinuses.', 'Please rate the following on a scale from 1 to 5: Sinus disorder', '', 1, 1, 0, '0000-00-00 00:00:00'),
(727, 'Sleep apnea', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by cessation of breathing for short periods during sleep.', 'Please rate the following on a scale from 1 to 5: Sleep apnea', '', 1, 1, 0, '0000-00-00 00:00:00'),
(728, 'Sneezing', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by the involuntary expulsion of air from the nose.', 'Please rate the following on a scale from 1 to 5: Sneezing', '', 1, 1, 0, '0000-00-00 00:00:00'),
(729, 'Sore throat', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by of marked discomfort in the throat', 'Please rate the following on a scale from 1 to 5: Sore throat', '', 1, 1, 0, '0000-00-00 00:00:00'),
(730, 'Stridor', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by a high pitched breathing sound due to laryngeal or upper airway obstruction.', 'Please rate the following on a scale from 1 to 5: Stridor', '', 1, 1, 0, '0000-00-00 00:00:00'),
(731, 'Tracheal fistula', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by an abnormal communication between the trachea and another organ or anatomic site.', 'Please rate the following on a scale from 1 to 5: Tracheal fistula', '', 1, 1, 0, '0000-00-00 00:00:00'),
(732, 'Tracheal mucositis', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by an inflammation involving the mucous membrane of the trachea.', 'Please rate the following on a scale from 1 to 5: Tracheal mucositis', '', 1, 1, 0, '0000-00-00 00:00:00'),
(733, 'Tracheal stenosis', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by a narrowing of the trachea.', 'Please rate the following on a scale from 1 to 5: Tracheal stenosis', '', 1, 1, 0, '0000-00-00 00:00:00'),
(734, 'Voice alteration', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by a change in the sound and/or speed of the voice.', 'Please rate the following on a scale from 1 to 5: Voice alteration', '', 1, 1, 0, '0000-00-00 00:00:00'),
(735, 'Wheezing', '', 'Respiratory, thoracic and mediastinal disorders', 'A disorder characterized by a high-pitched, whistling sound during breathing. It results from the narrowing or obstruction of the respiratory airways.', 'Please rate the following on a scale from 1 to 5: Wheezing', '', 1, 1, 0, '0000-00-00 00:00:00'),
